import requests

def get_search_status(request_id):
    url = f'https://api.scrapegraphai.com/v1/searchscraper/{request_id}'
    headers = {
        'SGAI-APIKEY': 'YOUR_API_KEY'
    }

    response = requests.get(url, headers=headers)

    if response.status_code == 200:
        data = response.json()
        status = data['status']

        if status == 'completed':
            return {
                'success': True,
                'status': status,
                'result': data['result'],
                'reference_urls': data['reference_urls']
            }
        elif status == 'failed':
            return {
                'success': False,
                'status': status,
                'error': data['error']
            }
        else:
            return {
                'success': None,
                'status': status
            }
    else:
        return {
            'success': False,
            'error': f'Request failed with status code: {response.status_code}'
        }

# Example usage
request_id = 'YOUR_REQUEST_ID'
status = get_search_status(request_id)

if status['success'] is True:
    print('Search completed successfully!')
    print('Results:', status['result'])
    print('Reference URLs:', status['reference_urls'])
elif status['success'] is False:
    print('Search failed:', status.get('error', 'Unknown error'))
else:
    print(f'Search is {status["status"]}...')

